import { useState, useCallback } from 'react';

/* use undefined instead of null, cuz of type checking */
export default (initValue:string|number|undefined = undefined) => {
    /* unpack value and state setter from useState */
    const [value, setter] = useState(initValue);
    /*  not sure about concept of callback yet */
    /* what should type of e be?? */
    const handler = useCallback( (e) => {
        setter(e.target.value);
    }, []);
    return [value, handler];
}
